var VideoPlayer = new Vue({
    el: "#videoplayer",

    data: {
        node: $("#videoplayer"),
        source: false,
        width: 0,
        height: 0,

        default: {
            width: 800,
            height: 600
        }
    },

    created: function() {

    },

    methods: {
        show: function() {
            this.node.show();
        },

        hide: function() {
            this.node.hide();
            this.source = false;
        },

        play: function(source, width, height) {
            this.width = width || this.default.width;
            this.height = height || this.default.height;

            this.source = "http://youtube.com/embed/" + this.parseSource(source);
            this.show();
        },

        parseSource: function(url) {
            var regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|\&v=)([^#\&\?]*).*/;
            var match = url.match(regExp);

            if (match && match[2].length == 11) {
                return match[2];
            } else {
                return false;
            }
        }
    }
});
